const mongoose = require("mongoose");
const Schema = mongoose.Schema;
const passportLocalMongoose = require("passport-local-mongoose");

// passportLocalMongoose defines a username, hash and salt field for storing the username, the hashed password and the salt value. Additionally Passport-Local Mongoose adds some methods to your Schema.
const userSchema = new Schema({
    email: { type: String, required: true, unique: true },
    profileImage: {
        url: {
            type: String,
            default:
                "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/271deea8-e28c-41a3-aaf5-2913f5f48be6/de7834s-6515bd40-8b2c-4dc6-a843-5ac1a95a8b55.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..BopkDn1ptIwbmcKHdAOlYHyAOOACXW0Zfgbs0-6BY-E",
        },
        filename: { type: String },
    },
    firstname: { type: String, required: true },
    lastname: { type: String, required: true },
    phone: { type: Number, required: true },
    address: { type: String, required: true },
    city: { type: String, required: true },
    state: { type: String, required: true },
});

userSchema.plugin(passportLocalMongoose);

module.exports = mongoose.model("User", userSchema);
